var routing_table = {};

var defaultHandler = function(req, resp) {
	var body = '<h1>The page you requested could not be found</h1>';
	resp.writeHead(
		200,
		{
			'Content-Length': body.length,
			'Content-Type': 'text/html'
		}
	);
	resp.end(body);
}
routing_table['default_handler'] = defaultHandler;

function get_handler(str) {
	return routing_table[str] || defaultHandler;
}


exports.get_handler = get_handler;
